# stage2 (pyStage, converted from Scratch 3)

from pystage.en import Sprite, Stage

stage = Stage()
stage.add_backdrop('backdrop1')
stage.add_backdrop('city_with_water')
stage.add_backdrop('boardwalk')
stage.add_backdrop('basketball_2')
stage.add_backdrop('farm')
stage.create_variable('my variable', 0)
amon = stage.add_a_sprite(None)
amon.set_name("Amon")
amon.set_x(280)
amon.set_y(0)
amon.go_to_back_layer()
amon.go_forward(1)
amon.add_costume('amon', center_x=480, center_y=360, factor=2)
amon.add_sound('pop')

def when_program_starts_1(self):
    self.go_to_back_layer()
    self.go_to_x_y(0.0, 0.0)
    while True:
        self.change_x_by(-5.0)
        if (self.x_position() < -460):
            self.go_to_x_y(465.0, 0.0)

amon.when_program_starts(when_program_starts_1)
anina_dance = stage.add_a_sprite(None)
anina_dance.set_name("Anina Dance")
anina_dance.set_x(-150)
anina_dance.set_y(-48)
anina_dance.go_to_back_layer()
anina_dance.go_forward(2)
anina_dance.add_costume('anina_stance', center_x=76, center_y=252, factor=2)
anina_dance.add_costume('anina_top_stand', center_x=74, center_y=280, factor=2)
anina_dance.add_costume('anina_top_r_step', center_x=248, center_y=272, factor=2)
anina_dance.add_costume('anina_top_l_step', center_x=228, center_y=274, factor=2)
anina_dance.add_costume('anina_top_freeze', center_x=110, center_y=268, factor=2)
anina_dance.add_costume('anina_r_cross', center_x=126, center_y=268, factor=2)
anina_dance.add_costume('anina_pop_front', center_x=68, center_y=270, factor=2)
anina_dance.add_costume('anina_pop_down', center_x=74, center_y=156, factor=2)
anina_dance.add_costume('anina_pop_left', center_x=238, center_y=266, factor=2)
anina_dance.add_costume('anina_pop_right', center_x=66, center_y=268, factor=2)
anina_dance.add_costume('anina_pop_l_arm', center_x=68, center_y=274, factor=2)
anina_dance.add_costume('anina_pop_stand', center_x=76, center_y=276, factor=2)
anina_dance.add_costume('anina_pop_r_arm', center_x=88, center_y=272, factor=2)
anina_dance.next_costume()
anina_dance.next_costume()
anina_dance.next_costume()
anina_dance.next_costume()
anina_dance.add_sound('dance_celebrate')
anina_dance.add_sound('dance_magic')

def when_program_starts_2(self):
    while True:
        self.go_to_random_position()
        self.hide()
        self.wait(1.5)
        self.show()
        self.set_size_to(100.0)
        self.say_for_seconds("Hello!", 1.0)
        self.wait(2.0)
        self.hide()
        self.switch_backdrop_to("city_with_water")
        self.wait(1.0)
        self.show()
        self.switch_costume_to("anina_pop_r_arm")
        self.switch_backdrop_to("boardwalk")
        self.wait(1.0)
        self.switch_backdrop_to("backdrop1")
        self.switch_costume_to("anina_pop_left")
        self.wait(1.0)
        self.switch_costume_to("anina_top_freeze")
        self.switch_backdrop_to("basketball_2")
        self.wait(1.0)
        self.switch_costume_to("anina_r_cross")
        self.set_size_to(100.0)
        self.switch_backdrop_to("farm")

anina_dance.when_program_starts(when_program_starts_2)
stage.play()
