def ridgeindex(width,depth,ridgedepth=None,sell='ln',half=False,s='',plot=False,ref=False,res=0.1,fit=False):
    from modes import modesolver
    from waves import Wave
    from sellmeier import index
    ridgedepth = ridgedepth if ridgedepth is not None else (depth/2 if half else depth)
    args = dict(width=width,depth=depth,ape=ridgedepth,res=res)
    # wx = np.linspace(400,2000,33)
    wx = np.linspace(400,2000,17)
    # wx = np.linspace(400,2000,9)
    print('width,depth,ridgedepth',width,depth,ridgedepth,'args',args)
    n1 = Wave([modesolver(x,**args,sell=sell+'ridge'+s).neff - index(x,sell+s) for x in wx],wx,'n1')
    if plot:
        n0 = Wave(index(wx,sell+'ridgewg'+s)-index(wx,sell+s),wx,'n0')
        Wave.plots(n0,n1,x=' (nm)',y='index')
    if fit:
        return Wave(n1.y,n1.x/1000).quadraticfit(coef=1)
    return n1.rename(f'{width}x{depth}') if not ref else n0.rename('reference')
