    sizes = np.linspace(1,12,12)
    wds  = [(int(w),int(d)) for w in sizes for d in sizes]
    cs = [ridgeindex(w,d,sell=sell,half=half,s='y' if y else '',res=0.1,fit=1) for w,d in wds]
    with open('ridgesellmeier.txt', 'a') as f:
        f.write(f"f{sell}{'half'*half}ridge{'y' if y else 'z'} = {{\n")
        for w in sizes:
            f.write('    '+''.join([f"{k}:{lmap(PPfloat,v)}, " for k,v in zip(wds,cs) if k[0]==w])+'\n')
        f.write(f"}}\n")
